import React from "react";

const Todo = (props)=>{
    const {id,status,text} = props;
    const [cstatus, setstatus] = React.useState(status);
    const changestate = ()=>{

      if (cstatus){
          setstatus(false);
      }else{
        setstatus(true);
      }
    }
    return(
        <div class="todo" onDoubleClick={changestate}>
            <span class={cstatus ? ("spanc") : ("spannc")} >{cstatus ? ("Completed") : ("not yet Completed")}</span>
            <h1>{text}</h1>
        </div>
    );
}

export default Todo;
